var Mongoose = require('mongoose');

var InnotekTobaccoDb = Mongoose.createConnection('mongodb://localhost/innotek_tobacco');
var InnotekDevFwDb = Mongoose.createConnection('mongodb://localhost/innotek_device_fw');

InnotekTobaccoDb.on('error', function(err){

	console.log(err);
});

InnotekTobaccoDb.once('open', function(){

	console.log("Open innotek_tobacco db ok!");
});

InnotekDevFwDb.on('error', function(err){

	console.log(err);
});

InnotekDevFwDb.once('open', function(){

	console.log("Open innotek_device_fw db ok!");
});

module.exports.InnotekTobaccoDb = InnotekTobaccoDb;
module.exports.InnotekDevFwDb = InnotekDevFwDb;
module.exports.Schema = Mongoose.Schema;