var Mongoose = require('../db');

var Schema = Mongoose.Schema;

var schema = new Schema({

     ackType:       Number
   , address:       String
   , midAddress:    String
   , result:        Number
   , version:       String
   , upDateAt:      Number
   , createAt:      Date
});

var Feedback = Mongoose.InnotekDevFwDb.model('update_feedbacks', schema);


module.exports.insertFeedback = function(feedback){

    Feedback.findOneAndUpdate(feedback, feedback, function(err, data){
        
        if (err)
        {
            console.log("save update feedbacks failed");
        }
        else
        {
            console.log("save update feedbacks successful");
        }
    });   	
};