var Fs = require('fs');

module.exports.sendWebFile = function(filePath, res, next){

	Fs.readFile(filePath, function(err, data){
	
		if (err)
		{
			console.error(err);			
			res.writeHead(404);
			res.write("404");
			res.end();
			next();
			return;
		}
		
		res.setHeader('Content-Type', 'text/html');
		res.writeHead(200);
		res.write(data);
		res.end();
		next();
	});
}

